class Solution {
public:
    int lengthOfLongestSubstring(string s) {
        unordered_set<char>sub;//set to store occoured character
        int left=0,right=0; //two pointers to navigate
        int max_size=0;
        while(left<s.size() && right<s.size())
        {
            //if the char is not in the subset
            //put it in the set
            //find the max size
            if(sub.find(s[right])==sub.end())
            {
                sub.insert(s[right]);
                int n=sub.size();
                max_size=max(max_size,n);
                right++;
            }
            //if already there 
            //shrink the window from left 
            //until the repeated char is not omitted from left
            else
            {
                sub.erase(s[left]);
                left++;
            }
        }
        return max_size;
    }
};
