[@sujitg0124](/sujitg0124) class Solution {
    public int lengthOfLongestSubstring(String s) {
        if(s.isEmpty()) return 0;

        int ans = Integer.MIN_VALUE;
        int left = 0;
        int right = 0;

        while(right < s.length()) {
            if(hasDuplicates(s.substring(left, right + 1))) {
                ans = Math.max(ans, s.substring(left, right).length());
                left++;
            }
            else {
                ans = Math.max(ans, s.substring(left, right + 1).length());
            }

            right++;
        }

        return ans;
    }

    private boolean hasDuplicates(String s) {
        Set<Character> set = new HashSet<>();

        for(int i = 0; i < s.length(); i++) {
            if(!set.add(s.charAt(i))) return true;
        }

        return false;
    }
}


