                if (nums[i] < vmin) {
                    vmin = nums[i];
                }
            }

            while (left >= 0 && nums[left] > vmin) { left--; };
            while (right < nums.size() && nums[right] < vmax) { right++; };

            shortest = right - left - 1;
        }

        return shortest;
    }
