class Solution {
public:
    int findUnsortedSubarray(vector<int>& nums) {
        vector<int> chk(nums.begin(), nums.end());              // copying into new vector
        sort(chk.begin(), chk.end());                          // sorting new vector
        int n = nums.size(), s = 0, e = n - 1;                // initalizing start and end
