```
/* An obvious approach is to sort in O(nlog(n)) and use the 2 pointer approach */
class Solution
{
public:
    int findUnsortedSubarray(vector<int>& a);
};

/* Returns the length of the min subarray sorting which sorts the entire arrray */
int Solution :: findUnsortedSubarray(vector<int>& a)
{
    // Get the size
    int n = a.size();

    // Handle the corner cases
    if(n==0 || n==1) return 0;

    // Create the dp matrix
    vector<int> minRight(n);
    vector<int> maxLeft(n);

    /* Dp Defintions */
    /* minRight[i] represents the minimum element to the right of i, including i */
    /*  maxLeft[i] represents the maximum element to the left of i, including i */

    // Fill the minRight Matrix from right to left
    minRight[n-1] = a[n-1];
    for(int i=n-2; i>=0; i--)
        minRight[i] = min(a[i], minRight[i+1]);

    // Fill the maxLeft matrix from left to right
    maxLeft[0] = a[0];
    for(int i=1; i<n; i++)
        maxLeft[i] = max(a[i], maxLeft[i-1]);

    // Get the leftmost point of indifference where a[i]!=minRight[i] 
    int leftIndex = -1;
    for(int i=0; i<n; i++)
        if(a[i]!=minRight[i])
        {
            leftIndex = i;
            break;
        }

    // Get the rightmost point of indifference where a[i]!=maxLeft[i]
    int rightIndex = -1;
    for(int i=n-1; i>=0; i--)
        if(a[i]!=maxLeft[i])
        {
            rightIndex = i;
            break;
        }

    // If any of them remain unchanged, it means the array is sorted
    if(leftIndex==-1 || rightIndex==-1) return 0;

    // Else return the length of the subarray b/w [leftIndex, rightIndex] 
    int length = rightIndex - leftIndex + 1;

    return length;
}
