        int minVal = nums[start];
        int maxVal = nums[start];

        for(int i=start; i<=end; i++)
        {
            if(nums[i] < minVal)
                minVal = nums[i];
            if(nums[i] > maxVal)
                maxVal = nums[i];
        }

        for(int i=0; i<start; i++)
        {
            if(nums[i] > minVal)
                start = i;
        }

        for(int i=end; i<n; i++)
        {
            if(nums[i] < maxVal)
                end = i;
        }

        return (end - start + 1);
    }

