class Solution {
public:
    int findUnsortedSubarray(vector<int>& nums) {

        vector<int> sorted = nums;  

        //Create a sorted array
        sort(sorted.begin(), sorted.end()); 

        //set lower and higher bound as 0
        int lb = 0, hb = 0; 


        //now just calculate from where array started being unsorted from start
        for (int i = 0; i < nums.size(); i++) { 
            if (nums[i] != sorted[i]) { 
                lb = i; 
                break;
            }
        } 

        //calculate from where array started brong unsorted from last
        for (int i = nums.size()-1; i >= 0; i--) { 
            if (nums[i] != sorted[i]) { 
                hb = i; 
                break;
            }
        } 

        //Array is already sorted
        if (hb == 0 and lb == 0) { 
            return 0;
        } 

        //else return (higher bound - lower bound) + 1
        return (hb - lb) + 1;
    }
}; 


