class Solution {
public:
    int findUnsortedSubarray(vector<int>& nums) {
        stack<int> stk;
        int s = nums.size(), e = 0, maxV = numeric_limits<int>::min();

        for (int i = 0; i < nums.size(); i++) {
            maxV = max(maxV, nums[i]);
            while(!stk.empty() && nums[stk.top()] > nums[i]) {
                s = min(s, stk.top());
                e = i;
                stk.pop();
            }

            stk.push(i);
            if (maxV > nums[i]) e = i;   
        }
        return max(e - s + 1, 0);
    }
};
