class Solution {
public:

    int findUnsortedSubarray(vector<int>& nums) {
        int n = nums.size();
		vector<int> v; // Using vector to simulate stack.
        int r = 0, l = n-1; // r will contain first wrong element pos from right and l will contain first wrong element pos from left.

		// calculating l value.
        for(int i=0; i<n; ++i){            
            while(!v.empty() and nums[v.back()] > nums[i]){
                l = v.back() < l ? v.back() : l;
                v.pop_back();
            }
            v.push_back(i);
        }

        v.clear();

		// calculating r value.
        for(int i=n-1; i>=0; --i){
            while(!v.empty() and nums[i] > nums[v.back()]){
                r = v.back() > r ? v.back() : r;
                v.pop_back();
            }
            v.push_back(i);
        }

        if(r == 0 and l == n-1) return 0; // if no changes in l and r means the array is already sorted, thus simply return 0;
        else return r - l + 1; // else answer will be difference of right pos and left pos + 1(adding 1 due to 0 based indexing).
    }
};

