class Solution {

	///  1 3 6 4 8 2 10 7 9 11 15

    public int findUnsortedSubarray(int[] nums) {
        int sp = 0;
        int ep = 0;
        for(int i=0; i<nums.length-1; i++){
            if(nums[i+1] < nums[i]){
                sp = i;                                  /// temporary sp (=6).......   (index = 2)
                break;
            }
        }
        for(int i=nums.length-1; i>0; i--){
            if(nums[i-1] > nums[i]){
                ep = i;                                  /// temporary ep (=10)....... (index = 6)
                break;
            }
        }

		/// find max and min values between temporary sp and temporary ep ([6,10])

        int max = Integer.MIN_VALUE; int min = Integer.MAX_VALUE;           
        for(int i=sp; i<=ep; i++){                             
            if(nums[i] > max){
                max = nums[i];                           /// max = 10
            }
            if(nums[i] < min){
                min = nums[i];                           /// min = 2
            }
        }                                     

		///  get final sp and ep by checking rest of the array

        int e = ep;
        for(int i=nums.length-1; i>=e; i--){
            if(nums[i] < max){
                ep = i;                                  /// ep = 9...........  (index = 8)
                break;
            }
        }
        int s = sp;
        for(int i=0; i<=s; i++){
            if(nums[i] > min){
                sp = i;                                  /// sp = 3............  (index = 1)
                break;
            }
        }
        if(ep-sp+1 == 1){
            return 0;
        }
        return ep-sp+1;                          /// 8-1+1 = 8
    }
}

