class Solution:
    def findUnsortedSubarray(self, A):
        n = len(A)

        #scan from left to right
        mono = [(-1, -math.inf)]
        for i in range(n):
            while A[i] < mono[-1][1]:
                mono.pop()
            if i == mono[-1][0] + 1:
                mono.append((i, A[i]))
        left = mono[-1][0] + 1

        #scan from right to left
        mono = [(n, math.inf)]
        for i in range(n-1,-1,-1):
            while A[i] > mono[-1][1]:
                mono.pop()
            if i == mono[-1][0] - 1:
                mono.append((i, A[i]))
        right = mono[-1][0] - 1

        return right-left+1 if right>left else 0
