class Solution {    
    //time= O(n), space=O(n)
    public int findUnsortedSubarray(int[] a) {

        ArrayDeque<Integer> st= new ArrayDeque();
        int n=a.length;

        //1.monotonically increasing stack
        int l=n;
        for(int i=0;i<n;i++ ) {
            while(!st.isEmpty()&&a[st.peek()] > a[i]) {
                l=Math.min(st.pop(),l);
            }    
            st.push(i);
        }

        //2.monotonically decreasing stack
        st.clear();
        int r=-1;        
        for(int i=n-1;i>=0;i-- ) {
            while(!st.isEmpty()&&a[st.peek()] < a[i]) {
                r=Math.max(st.pop(),r);
            }    
            st.push(i);
        }

       // System.out.println("l:" +l + " r:" + r);
        if(l==n && r==-1) return 0;
        return r-l+1; //length

    }
}
