class Solution {
public:
    int findUnsortedSubarray(vector<int>& nums) {



        // length of the vector:-
        int size = nums.size();

        // creating the vector that store the clone of nums
        vector<int> ans;
        for(int i = 0;i<size;i++){
            ans.push_back(nums[i]);
        }

        // sort the vector 
        sort(ans.begin(),ans.end());

        // index's
        int start = size;
        int end = 0;

        // final loop containg conditions
        for(int i = 0;i<size;i++){
            if(nums[i] != ans[i]){
                start = min(start,i);
                end = max(end,i);
            }
        }

        int finalAns = end - start + 1;
        if(finalAns < 0){
            return 0;
        }else return finalAns;
    }
// By ~ Shubham Verma
};
