```C++ []
class Solution {
public:

    bool outOfOrder(vector<int> &nums, int i){
        if(nums.size() == 1) return 0;
        int x = nums[i];

        if(i == 0) return x > nums[1];
        if(i == nums.size()-1) return x < nums[i-1];

        return x > nums[i+1] or x < nums[i-1];
    }
    int findUnsortedSubarray(vector<int>& nums) {
        int s = INT_MAX, l = INT_MIN;

        for(int i = 0; i<nums.size(); i++){
            int x = nums[i];

            if(outOfOrder(nums, i)){
                s = min(s, x);
                l = max(l, x);
            }
        }
        if(s == INT_MAX) return 0;

        int left = 0;
        while(s >= nums[left]) left++;

        int right = nums.size() - 1;
        while(l <= nums[right]) right--;

        return right - left + 1;
    }
};
```

