class Solution {
public int findUnsortedSubarray(int[] nums) {
    int minVal = Integer.MAX_VALUE, maxVal = Integer.MIN_VALUE;
    int n = nums.length;
    for(int i = 1; i < n; i++) {
        if(nums[i] < nums[i-1]) {
            minVal = Math.min(minVal, nums[i]);
        }
    }
    for(int i = n-2; i >= 0; i--) {
        if(nums[i] > nums[i+1]) {
            maxVal = Math.max(maxVal, nums[i]);
        }
