class Solution {
public int findUnsortedSubarray(int[] nums) {
    int minVal = Integer.MAX_VALUE, maxVal = Integer.MIN_VALUE;
    int n = nums.length;
        // Finding the first and last position of the unsorted subarray
    for(int i = 1; i < n; i++) {  // for decreasing values
        if(nums[i] < nums[i-1]) {
            minVal = Math.min(minVal, nums[i]);
        }
    }
    for(int i = n-2; i >= 0; i--) { // for increasing values
        if(nums[i] > nums[i+1]) {
            maxVal = Math.max(maxVal, nums[i]);
        }
    }
    if(minVal == Integer.MAX_VALUE && maxVal == Integer.MIN_VALUE) return 0; // checking if the array is already sorted, if yes then return 0

    // If array is not sorted
    int start = 0, end = n-1;
    for(; start < n; start++) { // finding start point
        if(nums[start] > minVal) break; // find index in the left for a number greater than the min value found
    }
    for(; end >= 0; end--) { // finding end point
        if(nums[end] < maxVal) break; // find index in the right for a number lesser than the max value found
    }
    return end - start + 1; // ans: size of unsorted subarray
}
}


