class Solution {
public:
    int findUnsortedSubarray(vector<int>& nums) {

        int n = nums.size();

        int i = 0;

        int j = n - 1;

        // find the index where sorted condition is violated from both start and end

        while(i + 1 < n && nums[i] <= nums[i + 1])
            i++;

        while(j > 0 && nums[j] >= nums[j - 1])
            j--;

        // if array is already sorted

        if(i >= n - 1)
            return 0;

        // find the maximum and minimum element between violated index (i <= k <= j)

        int maxi = INT_MIN;

        int mini = INT_MAX;

        for(int k = i; k <= j; k++)
        {
            maxi = max(maxi, nums[k]);

            mini = min(mini, nums[k]);
        }

        int start = 0;

        int end = n - 1;

        // now find the first index from start where the elememt is not in correct position

        while(start <= i && nums[start] <= mini)
            start++;

        // find the last index from end where the element is not in correct position

        while(end >= j && nums[end] >= maxi)
            end--;

        return end - start + 1;
    }
};
