class Solution {
public:
    int findUnsortedSubarray(vector<int>& nums) {
        int n = nums.size();

        int mxNum = nums[0], mnNum = nums[0];
        for(int i=0;i<n;i++){
            mxNum = max(mxNum, nums[i]);
            mnNum = min(mnNum, nums[i]);
        }
        mnNum = min(mnNum, 0);
        if(mnNum < 0) mnNum *= -1;

        vector<int>dp((mxNum < 0 ? 0 : mxNum) + mnNum + 2, 0);
        for(int i=0;i<n;i++){
            dp[ nums[i] + mnNum ]++;
        }

        int rem[n], id = 0;
        for(int i = 0; i < mxNum + mnNum + 2; i++){
            while(dp[i]--){
                rem[id++] = i - mnNum;
            }
        }

        assert(id == n);

        int cnt = 0;
        for(int i=0;i<n;i++){
            if(rem[i] != nums[i]){
                break;
            }
            cnt++;
        }

        if(cnt==n) return 0;

        for(int i=n-1;i>=0;i--){
            if(rem[i] != nums[i]){
                break;
            }
            cnt++;
        }

        return n - cnt;
    }
};
```

