class Solution {
public:
    int findUnsortedSubarray(vector<int>& nums) {
        vector<int> temp = nums;
        sort(nums.begin(), nums.end());

        int start = 0;
        for(int i = 0; i < nums.size(); ++i){
            start = i;
            if(temp[i] != nums[i]){
                break;
            }
        }

        int end = nums.size() - 1;
        for(int i = nums.size() - 1; i > -1; --i){
            end = i;
            if(temp[i] != nums[i]){
                break;
            }
        }

        if(end <= start){return 0;}
        return end - start + 1;
    }
};
```

