class Solution {
public:
    int findUnsortedSubarray(vector<int>& v) 
    {
        int n=v.size();
        int min,max;
        int i=0;
        int e=n-1;
        int s=0;
        int ans;
        int j=v.size()-1;
        bool f=false;

       for (s = 0; s < n-1; s++)
        {
         if (v[s] > v[s+1])
            break;
        }
        if (s == n-1)
        {
            return 0;
        }

        while(i<n)
        {

            if(v[i]>v[i+1])
             {
                s=i;
                break;
              }
            else
            {
                i++;
            }
        }
        while(j>=0)
        {

            if(v[j]>v[j-1])
            {
                j--;
            }
            if(v[j]<v[j-1])
            {
                e=j;
                break;
            }
        }
    max=v[s];
    min=v[s];
    for(int i=s+1;i<e;i++)
    {
        if(v[i]>max)
        {
            max=v[i];
        }
        if(v[i]<min)
        {
            min=v[i];
        }
    }

     for(int i=0;i<s;i++)
    {
        if(v[i]>min)
        {
            s=i;
            break;
        }
    }


    for( i = n -1; i >= e+1; i--)
    {
         if(v[i] < max)
        {
            e = i;
            break;
        }
    } 
   ans=e-s+1;
   return ans;

    }
};
