public int findUnsortedSubarray(int[] nums) {
        int n= nums.length;
        int start = -1, end = -2;
        int max = nums[0], min = nums[n - 1];

        // find two pointers in one loop
        for(int i = 1; i <= n -1; i++){
            max = Math.max(max, nums[i]);
            min = Math.min(min, nums[n - i - 1]);

            if(max > nums[i]) end = i;        //if the current num is not bigger than previous one

            if(min < nums[n - i - 1]) start = n - i - 1;   //if the current num is not smaller than next one
        }
        return end - start + 1;
    }
