int findUnsortedSubarray(vector<int>& nums) {

        if(nums.size()<=1){
            return 0;
        }

        int n = nums.size();
        int ans = n;
        vector<int>sortedArr(nums);

        sort(sortedArr.begin(),sortedArr.end());

        int i =0,j=n-1;
        bool start = true;
        bool end =true;

        while(i<=j){

            if(nums[i]==sortedArr[i] && start){
                i++;
                --ans;
            }

            else{
                start = false;
            }

            if(nums[j]==sortedArr[j] && end && i<j){
                j--;
                --ans;
            } 

            else{
                end = false;
            }

            if(start == false && end == false){
                return ans;
            }

        }

        return ans;
    }
};

