# Code
```
/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode() : val(0), next(nullptr) {}
 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */
class Solution {
public:
    ListNode* deleteDuplicates(ListNode* head) {
        // Handling the Edge Case of empty list
        if(head == NULL) 
            return head;
        ListNode* temp = head;
        // This is our temporary variable 
        // We will use it to iterate through the list
        // While the head pointer remains intact

        while(temp->next != NULL){
            // Since we have to compare the next node's value
            // Our main condition is whether next node is NULL
            if(temp->next->val == temp->val){
                temp->next = temp->next->next;
                // Skip over the node connecting to next one
            }
            else{
                temp = temp->next; // Simply move through the list
            }
        }
        return head;
    }
};
```

