public void moveZeroes(int[] nums) {
        int shifter= 0;
        for(int i=0;i<nums.length;i++){
            if(nums[i]!= 0) nums[shifter++] = nums[i];
        }
        while(shifter<nums.length) nums[shifter++] =0;
    }
