public static void MoveZeros(int[] nums)
{
	if (nums == null || nums.Length <= 1)
		return;

	int curr = 0;
	int next = 1;

	while (next <= nums.Length - 1)
	{
		if (nums[curr] == 0)
		{
			if (nums[next] == 0)
			{
				next++;
				continue;
			}
			else
			{
				nums[curr] = nums[next];
				nums[next] = 0;
			}
		}
		curr++;
		next++;
	}
}
