class Solution {
    private void swap(int[] arr,int i,int j){
        int temp = arr[i];
        arr[i] = arr[j];
        arr[j] = temp;
    }
    public void moveZeroes(int[] nums) {
        int k = 0;
        int n = nums.length;
        for(int i=0;i<n;i++){
            if(nums[i] != 0){
                swap(nums,i,k++);
            }
        }
    }
}
