class Solution:
    def canConstruct(self, ransomNote: str, magazine: str) -> bool:
        """
            Idea: Frequency Count
            Time: O(n + m)
            Space: O(n)
        """
        c = {}
        for i in ransomNote:
            c[i] = c.get(i,0) + 1
        for k in magazine:
            if c.get(k,0):
                c[k] -= 1
                if c[k] ==0: c.pop(k)
        if not c: return True
        return False
