impl Solution {
    pub fn can_construct(ransom_note: String, magazine: String) -> bool {
        if ransom_note.len() > magazine.len() {
            return false;
        }

        let mut map = [0; 26];
        let mut flags = 0;
        let idx = |c: char| c as usize - 'a' as usize;

        ransom_note.chars().for_each(|c| {
            let i = idx(c);
            map[i] += 1;
            flags |= 1 << i; // set flag
        });

        for c in magazine.chars() {
            let i = idx(c);
            map[i] -= 1;
            if map[i] == 0 {
                flags ^= 1 << i;  // drop flag
                if flags == 0 {
                    return true;
                }
            }
        }
        false
    }
}
```

