class Solution {
    public String smallestGoodBase(String n) {
        long num = Long.valueOf(n);
        //b stands for the base, d stands for the # of digits. f(b,d)=1+b+b^2+...+b^(d-1) = (b^d -1 )/(b-1)
        //based on this above, d has to be less than log(num+1) and bigger than 1
        //Then within the inner loop when d is fixed, calculate the mathematical boundaries for the upper and lower limits amd perform binary search
        for (int d = (int)(Math.log(num + 1) / Math.log(2)); d > 2; d--) {
            long l = (long)(Math.pow(num + 1, 1.0 / d));
            long r = (long)(Math.pow(num, 1.0 / (d - 1)));

            while (l <= r) {
                long k = l + ((r - l) >> 1);
                long f = 0L;
                for (int i = 0; i < d; i++) {
                    f = f * k + 1;
                }
                if (num == f) {
                return String.valueOf(k);
                } else if (num < f) {
                r = k - 1;
                } else {
                l = k + 1;
                }
            }
        }

        return String.valueOf(num - 1);
    }    
}


