    // Return the resolution, or -1 if no integer resolution
    int Solve(long n, int len)
    {
        // Binary search to find the resolution
        long left=1, right=(long)pow((double)0xFFFFFFFF, 2.0/(len-1)), mid;
        while(left<=right)
        {
            mid=left+(right-left)/2;
            long sum=1, cur=mid;
            bool overflow=false;
            for(int i=0; i<len-1; )
            {
                sum+=cur;
                if(++i==len-1) break;
                if(((double)cur)*mid+sum>LONG_MAX)
                {
                    overflow=true;
                    break;
                }
                cur*=mid;
            }
            if(overflow || sum>n)
                right=mid-1;
            else if(sum<n)
                left=mid+1;
            else
                return mid;
        }
        return -1;
