# Runtime: 2912 ms
def sieve(P):
    primes = [False, False, True] + [True] * (P - 3)
    for i in range(2, math.ceil(math.sqrt(P))+1):
        if primes[i]:
            j = i * i
            while j < len(primes):
                primes[j] = False
                j += i
    return primes

primes = sieve(10**5)

class Solution:
    def smallestGoodBase(self, N: str) -> str:

        def factors(n):
            f = []
            while n > 1:
                if n < len(primes) and primes[n]:
                    f.append(n)
                    break

                if n % 2 == 0:
                    f.append(2)
                    n = n // 2
                    continue

                for i in range(3, math.ceil(math.sqrt(n))+1, 2):
                    if (n % i) == 0:
                        f.append(i)
                        n = n // i
                        break
                else:
                    f.append(n)
                    break
            return f

        def all_bases(f):
            res = []
            def helper(i, base):
                nonlocal res
                if i == len(f):
                    if base != 1:
                        res.append(base)
                    return None
                helper(i+1, base)
                helper(i+1, base*f[i])
            helper(0, 1)
            return sorted(set(res))

        def is_all_ones(base):
            n = int(N)
            while n:
                if n % base == 1:
                    n = n // base
                else:
                    return False
            return True

        f = factors(int(N) - 1)
        bases = all_bases(f)
        for base in bases:
            if is_all_ones(base):
                return str(base)
