            while(l<=r) {
                int64_t mid = l+(r-l)/2;
                unsigned long long b=1, sum=0;
                for(int j=0;j<=i;j++,b*=mid) if((sum+=b)>num) break;
                if(sum == num) return to_string(mid);
                else if(sum < num) l = mid+1;
                else r = mid-1;
            }
        }
