class Solution {
public:
    unsigned long long findBase(unsigned long long n, int d){
        unsigned long long low = 1;
        unsigned long long high = (unsigned long long)(pow(n, 1.0/d) + 1);

        while(low <= high){
            unsigned long long mid = low + (high-low)/2;
            unsigned long long sum = 1;
            unsigned long long curr = 1;

            for(int i=1; i<=d; i++){
                curr = curr*mid;
                sum = sum + curr;
            }

            if(sum == n)  return mid;
            else if(sum > n)  high = mid-1;
            else  low = mid+1;
        }

        return 0;
    }

    string smallestGoodBase(string n) {
        unsigned long long n1 = stoll(n);
        unsigned long long x = 1;

        for(int i=63; i>=1; i--){
            if((x << i) < n1){
                unsigned long long curr = findBase(n1, i);

                if(curr != 0)
                    return to_string(curr);
            }
        }

        return to_string(n1 - 1);
    }
};
