impl Solution {
    pub fn smallest_good_base(n: String) -> String {
        let n = n.parse::<u64>().unwrap();
        let mut m_max = (n as f64).log(2.0).floor() as u32;
        while m_max > 1 {
            let k = (n as f64).powf(1.0 / m_max as f64).floor() as u64;
            if k > 1 {
                let mut sum = 1;
                let mut mul = 1;
                for _ in 0..m_max {
                    mul *= k;
                    sum += mul;
                }
                if sum == n {
                    return k.to_string();
                }
            }
            m_max -= 1;
        }
        (n - 1).to_string()
    }
}
```

