def smallestGoodBase(self, n: str) -> str:
    n = int(n)
    max_len = ceil(log2(n))
    ans = n
    for l in range(1, max_len + 1):
        L = 2; R = n
        while L < R:
            M = (L+R)//2
            Sum = (M**l - 1)//(M - 1)
            if Sum < n:
                L = M+1
            else:
                R = M
        if (L**l - 1)//(L - 1) == n:
            ans = min(ans, L)

    return str(ans)
