#define ll long long

class Solution {
public:

    ll check(int bits, ll number, ll mid) {
        ll m = number - 1;
        ll curr = 1;
        int breakBit = bits;
        for(int i = 1; i < bits; i++) {

            if(m / curr < mid) {
                breakBit = i;
                break;
            }

            curr *= mid;
            m -= curr;
        }

        if(breakBit == bits) {
            return m == 0 ? 1 : 0;
        } else {
            return 2;
        }

    }


    ll search(int bits, ll number) {

        ll lo = 2;
        ll hi = number;


        while(lo + 1 < hi) {
            ll mid = (lo + hi) / 2;

            int res = check(bits, number, mid);

            if(res == 2) {
                hi = mid;
            } else {
                lo = mid;
            }
        }

        if(check(bits, number, lo) == 1) return lo;
        if(check(bits, number, hi) == 1) return hi;
        return -1;
    }


    string smallestGoodBase(string n) {

        ll number = 0;

        for(int  i = 0; i < n.length(); i++) {
            number = number * 10 + (n[i] - '0');
        }

        ll base;

        for(int bits = 60; bits >= 2; bits--) {

            base = search(bits, number);
            if(base != -1) break;

        }

        return to_string(base);
    }
};
