import java.math.BigInteger;

class Solution {
    public String smallestGoodBase(String n) {
        long num = Long.parseLong(n);
        BigInteger N = BigInteger.valueOf(num);
        int maxExp = (int)(Math.log(num + 1)/Math.log(2));
        for (int exp = maxExp; exp >= 2; exp--) {
            long left = 2;
            long right = (long)(Math.pow(num, 1.0 / (exp - 1)));
            while (left <= right) {
                long mid = left + (right - left) / 2;
                long base = mid;
                BigInteger sum = BigInteger.valueOf(1);
                for (int i = 1; i < exp; i++) {
                    sum = sum.multiply(BigInteger.valueOf(base)).add(BigInteger.ONE);
                }
                if (sum.equals(N)) {
                    return Long.toString(base);
                }
                else if (sum.compareTo(N) == 1) {
                    right = mid - 1;
                }
                else {
                    left = mid + 1;
                }
            }
        }
        return Long.toString(num - 1);
    }
}
