    private long longestGood(long n){

		//min_base is so big
		long min_base = (long)1e18;
        long hi = n-1;
		for(int k=2;k<=61;k++) {

			int pow = k-1;
			long lo = 2;
			//long hi = n-1;
			while(lo < hi) {
				long mid = lo+hi>>1;
				int hp = (int) (Math.log10(n)/Math.log10(mid));

				//is hp too big or equal
				if(hp < pow || compute(mid, k-1, n) >= n) {
					hi=mid;
				}else {
					lo=mid+1;
				}
			}

            hi=lo;
			if(compute(lo, k-1, n)==n ) {
				min_base = Math.min(min_base, lo);
                //return min_base;
			}
            if(hi==2) break;
		}

		return min_base;
    }

    private static long compute(long mid, int k, long n) {

		long ret = 1;
		while(k-->0) {
			ret = ret*mid + 1;
		}
		return ret;
	}
