object Solution {
    def smallestGoodBase(n: String): String = {
        // 1 + b + b^2 + ... + b^m = n
        // try some m
        // m: 1, 2, 3, 4, 5, ..., n
        // Math.max(root(n, m + 1), 2) <= b <= root(n, m), use binary search to get b
		// actually m is bounded by log(10^18 < 2^(4*18)) = 4 * 18 = 72, so
        // time complexity = O(sum{72 * lg root(N, m), m = 1, 2, 3, ... 72}) = O(lgN) 
        val N: Long = n.toLong
        var ans: Long = N - 1
        def calc(e: Int, m: Long): Long = {
            var ans: Long = 1
            var accE: Long = e
            for {
                _ <- 0L until m
            } {
                ans += accE
                accE *= e
                if(ans > N) return N + 1
            }
            if(ans < N) return N - 1
            return N
        }
        var m: Long = 2
        var isContinue: Boolean = true
        while(m <= 72 && isContinue) {
            var left: Int = Math.max(Math.ceil(Math.pow(N, 1/(0.0 + m + 1))).toInt, 2)
            var right: Int = Math.pow(N - 1, 1/(0.0 + m)).toInt
            println(left, right, m)
            var mid: Int = -1
            if(right == 1) {
                isContinue = false
            }
            if(left <= right){
                if(calc(left, m) == N) {
                    ans = Math.min(ans, left)
                }
                while(left <= right) {
                    if(right - left <= 1) {
                        if(calc(right, m) == N) {
                            ans = Math.min(ans, right)
                            left += 1
                            right -= 1
                        }
                        else {
                            left += 1
                            right -= 1
                        }
                    }
                    else {
                        mid = left + (right - left) / 2
                        val c = calc(mid, m)
                        if(c >= N) {
                            right = mid
                        }
                        else if(c < N) {
                            left = mid
                        }
                    }
                }
            }
            m += 1
        }
        ans.toString
    }
}
