public int minDistance(String word1, String word2) {
        return minDistance(0,word1,0,word2);
    }
    private int minDistance(int p1, String word1, int p2, String word2) {
        int l1 = word1.length(), l2=word2.length();
        if(p1==l1) return l2-p2;
        if(p2==l2) return l1-p1;
        return word1.charAt(p1) == word2.charAt(p2) ? minDistance(p1+1,word1,p2+1,word2) : 1+Math.min(minDistance(p1+1,word1,p2,word2), minDistance(p1,word1,p2+1,word2));
    }
