        int l1 = word1.length(), l2=word2.length();
        int[][] dp=new int[l1+1][l2+1];
        for(int i=0;i<=l1;i++) 
            for(int j=0;j<=l2;j++) {
                if(i==0||j==0) dp[i][j]=i+j;
                else dp[i][j]=word1.charAt(i-1) == word2.charAt(j-1)? dp[i-1][j-1]:1+Math.min(dp[i-1][j],dp[i][j-1]);
            }
        return dp[l1][l2];
    }
