        for(int i=0; i<=m; i++)
        {
            for(int j=0; j<=n; j++)
            {
                if(i==0 || j==0)
                    dp[i][j] = i+j;

                else if(word1[i-1]==word2[j-1])
                    dp[i][j] = dp[i-1][j-1];

                else
                    dp[i][j] = 1+min(dp[i-1][j], dp[i][j-1]);
            }
        }
        return dp[m][n];  
    }
};
