        for(int i=0;i<=m;i++)
            dp[i][0]=0;
		//first column is seted to zero	

          for(int i=0;i<=n;i++)
            dp[0][i]=0;
        //first row is seted to zero
		//because initially no common sequence

        for(int i=1;i<=m;i++)
