```
class Solution {
public:

    int lcs(string X,string Y,int n,int m)
    {

      int dp[n + 1][m + 1];


	 for (int i = 0; i <= n; i++)
		for (int j = 0; j <= m; j++)
			if (i == 0 || j == 0)
				dp[i][j] = 0;

	 for (int i = 1; i <= n; i++)
		for (int j = 1; j <= m; j++)
			if (X[i - 1] == Y[j - 1]) 
				dp[i][j] = 1 + dp[i - 1][j - 1];
			else 
				dp[i][j] = max(dp[i][j - 1], dp[i - 1][j]);

	 return dp[n][m];

    }

    int minDistance(string word1, string word2) 
    {
        string s1,s2;

        if(word1.size()>=word2.size())
        {
            s1=word1;
            s2=word2;
        }
        else
        {
            s1=word2;
            s2=word1;
        }

        int n=s1.size();
        int m=s2.size();

        return n+m-2*lcs(s1,s2,n,m);
    }
};
```

