class Solution {
public:
    int dp[501][501]; //initialize table of len 501 as max ip is 500 as mentioned
    int res = 0; // to store the final result to be returned

    void initialize() {
        memset(dp, -1, sizeof(dp)); //initialize all cells with -1
    }

    int minDistance(string word1, string word2) {
        initialize(); // call func to initialize dp with -1
        int lcs = helper(word1, word2, word1.length(), word2.length());  //passing args to helper function aka LCS function
		// eg "sea" 
		//    "eat" **maximum**  common elements in both are 'ea' as subsequences are concerned   
		//therefore the number of operations to make the strings equal 
		//will be deleting the unique elements which will be equal to the 
		//length of both strings - the common elements in both of them 
		//(here sequence of common elements is important) that's why we call it a subsequence 
        res = word1.length() + word2.length() - 2*lcs; 
        return res;
    }

    int helper(string s1, string s2, int n, int m) {
        if(n == 0 || m == 0) return 0; //base case for lcs len of either string is 0 
        if(dp[n][m] != -1) return dp[n][m]; //this value is precomputed so we return from dp matrix instead of calculating again
        if(s1[n-1] == s2[m-1]) //if char at that index match
            return dp[n][m] = 1 + helper(s1, s2, n-1, m-1); //add 1 to result and call to word1.len - 1 and word2.len - 1
        else
            return dp[n][m] = max(helper(s1, s2, n-1, m), helper(s1, s2, n, m-1)); // if char do not match shorten the len of either one and calculate res in the end return the one with the maximum common elements until base case is satisfied
    }
};
