         vector<vector<int>> dp(m+1, vector<int>(n+1, 0));
        for(int i=0;i<=m;i++) {
            for(int j=0;j<=n;j++) {
                if(i == 0) // word1 ended now what we can do this remove characters of word2 and total of j no of characters of word2 we need to remove
                    dp[i][j] = j;
               else if(j == 0) dp[i][j] = i; // word2 ended now what we can do this remove characters of word1 and total i no of characters of word1 we need to remove

                else if(word1[i-1] == word2[j-1]) { // in case both character are same them copy previous solution
                    dp[i][j] = dp[i-1][j-1];
                }         
                // case in which both character are different then try removing the character from both of them and check which one you remove in min moves
                else dp[i][j] = 1 + min(dp[i][j-1], dp[i-1][j]); 
            }
        }
        //return dp[m][n] which gives you min operation to make word1 of m length and word2 of n length to make equal
        return dp[m][n];
    }
};
