                if (word1.charAt(i-1) == word2.charAt(j - 1)) {
                    dp[1][j] = dp[0][j - 1] + 1;
                } else {
                    dp[1][j] = Math.max(dp[0][j], dp[1][j - 1]);
                }
            }
            System.arraycopy(dp[1],0,dp[0],0,m+1);
        }
        return m + n-2*dp[1][m];
    }
