        if(word1.length()==0 && word2.length()==0){
            return 0;
        }
        if(word1.length()==0){
            return word2.length();
        }
        if(word2.length()==0){
            return word1.length();
        }
        if(word1.charAt(0)==word2.charAt(0)){
            return minDistance(word1.substring(1),word2.substring(1));
        }else{
            int a = minDistance(word1.substring(1),word2);
