class Solution {
private:
    int lcs(int m, int n, string &s, string &t) {
        if(m == 0 || n == 0) return 0;
        if(s[m-1] == t[n-1])
            return 1 + lcs(m-1, n-1, s, t);
        return max(lcs(m, n-1, s, t), lcs(m-1, n, s, t));
    }
public:
    int minDistance(string word1, string word2) {
        int m=word1.size(), n=word2.size();
        return m+n-2*lcs(m, n, word1, word2);
    }
};
