class Solution {
    public int minDistance(String word1, String word2) {
        int s1=word1.length();
        int s2=word2.length();
        int n = Math.max(s1,s2);
        StringBuilder sb =new StringBuilder(word2);
        int common =longestCommonSubsequence(word1,sb);
        int ans =0;
        if(s1>common) ans+=s1-common;
        if(s2>common) ans+=s2-common;

        return ans;
    }
     public int longestCommonSubsequence(String text1, StringBuilder text2) {
        int n = text1.length();
        int m = text2.length();
        int[][] dp =new int[n+1][m+1];

        for(int j = 0 ; j<=m ;j++) dp[0][j]=0;
        for(int i = 0  ; i <=n ;i++) dp[i][0]=0;


        for(int i = 1 ; i<=n ;i++){
            for(int j=1 ;j<=m ;j++){
                if(text1.charAt(i-1)==text2.charAt(j-1)) dp[i][j]= 1 + dp[i-1] [j-1] ;

                else dp[i][j]=Math.max(dp[i-1][j] , dp[i][j-1]);
            }
        }
        return dp[n][m];
    }
}

